<script>
	var ctx = document.getElementById("ctx").getContext("2d");
	ctx.font = '30px Arial';
	
	var TILE_SIZE = 32;
	var HEIGHT = 500;
	var WIDTH = 800;

	var timestarted = Date.now();
	var framecount = 0;
	var score = 0;

	var paused = false;
	
	var Img = {};
	Img.player = new Image();
	Img.player.src = "<?php echo base_url(); ?>assets/images/player.png";
	Img.bat = new Image();
	Img.bat.src = "<?php echo base_url(); ?>assets/images/bat.png";
	Img.bee = new Image();
	Img.bee.src = "<?php echo base_url(); ?>assets/images/bee.png";
